INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Open miK(rofon)','2025-2-23','20:00h','TapB','3,00 €','Predstava','<p>Splitski openmikovci u veljači potpuno novi repertoar izvode svake nedjelje. Svi zainteresirani se mogu po prvi put okušati u stand-upu ili nastaviti svoj već započeti komičarski put. Prijave za nastup u inbox SplickeScene ili na splickascena@gmail.com.</p><p>Ulaznice su u prodaji <a href="https://core-event.co/" target="_blank">online</a>, a na ulazu ako ih ostane.<br/></p>','https://www.facebook.com/splickascena');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Realizam ćorava sokaka vol. VIII','2025-2-23','','Etnografski muzej','0,00 €','Izložba','<p>Izložba &#34;Suvremena šokačka avangarda / Realizam ćorava sokaka vol. VIII&#34; autora Mislava Lešića istražuje spoj slavonske tradicije i suvremenog umjetničkog izraza. Autor kroz figurativno slikarstvo prikazuje elemente svakodnevice šokačkog sela, uključujući arhitekturu, portrete, narodne običaje i životne prizore.</p><p>Posebnost izložbe očituje se u kombinaciji realizma, psihodelične grafike i suvremenih stilskih pravaca, čime Lešić dokumentira nestajuće elemente slavonske kulture. Kroz ovaj projekt, koji traje od 2017. godine, Lešić spaja slikarstvo s glazbenim izričajem, što dodatno obogaćuje njegov umjetnički opus i doprinos očuvanju slavonske tradicije.</p>','https://www.facebook.com/profile.php?id=100063966137118');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Orilo, gorilo, ić’ se mora!','2025-2-23','','Etnografski muzej','0,00 €','Izložba','<p>Izložba &#34;Orilo, gorilo, ić’ se mora!&#34; autora dr. sc. Joška Ćalete i Marijane Botić Rogošić realizirana je u suradnji Instituta za etnologiju i folkloristiku, Lokalne akcijske grupe Zagora i Etnografskog muzeja Split.  Riječ je o izložbi o pokladnim običajima srednje Dalmacije - kako onima koji se nalaze na listi zaštićenih nematerijalnih dobara RH poput godišnjeg pokladnog ophoda podkamešničkih sela i pokladnog ophoda mačkara mućkog kraja tako i onima koje bi trebalo revitalizirati, obnoviti ili zaštititi.<br/></p><p>Izložba je otvorena do 28. veljače 2025.</p>','');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Realizam ćorava sokaka vol. VIII','2025-2-24','','Etnografski muzej','0,00 €','Izložba','<p>Izložba &#34;Suvremena šokačka avangarda / Realizam ćorava sokaka vol. VIII&#34; autora Mislava Lešića istražuje spoj slavonske tradicije i suvremenog umjetničkog izraza. Autor kroz figurativno slikarstvo prikazuje elemente svakodnevice šokačkog sela, uključujući arhitekturu, portrete, narodne običaje i životne prizore.</p><p>Posebnost izložbe očituje se u kombinaciji realizma, psihodelične grafike i suvremenih stilskih pravaca, čime Lešić dokumentira nestajuće elemente slavonske kulture. Kroz ovaj projekt, koji traje od 2017. godine, Lešić spaja slikarstvo s glazbenim izričajem, što dodatno obogaćuje njegov umjetnički opus i doprinos očuvanju slavonske tradicije.</p>','https://www.facebook.com/profile.php?id=100063966137118');
