INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Deserti u čaši','2025-2-26','17:00h','ŠKMER Akademija','70€','Edukacija','<p>Naučite kako slojevite deserte u čaši mogu biti idealan završetak svakog obroka.<br/></p>','https://skmer.hr/slasticarnica-srijedom-2024-25-termini-novih-tecajeva-za-istinske-ljubitelje-slasticarstva');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Dahomej','2025-2-26','17:00h','Kinoteka Zlatna vrata','3,50 €','Film','<p><span style="font-size: 14px;float: none;">Studeni 2021. Dvadeset i šest predmeta iz riznice Kraljevstva Dahomey uskoro će napustiti Pariz i vratiti se u zemlju porijekla, današnju Republiku Benin. Zajedno s tisućama drugih, te su artefakte opljačkale francuske kolonijalne trupe 1892. No, kakav će stav prema povratku tih predmeta, koji su pripadali njihovim precima, zauzeti zemlja koja je morala napredovati u njihovoj odsutnosti? Dok se duša artefakata oslobađa, mi svjedočimo gorljivoj raspravi među sveučilišnim studentima koji imaju bezbroj različitih mišljenja i dvosmislenih stajališta. </span><br/></p>','https://zlatnavrata.hr/dogadaj/dahomej/2452');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Kup: Hajduk - Rijeka','2025-2-26','17:00h','Poljud','od 4,00 €','Sport','<p>U prvenstvu Hajduk vodi bitku s Rijekom u samom vrhu ljestvice, ali u Kupu nema prilike za popravni. U nizu utakmica koje u samo osam dana mogu snažno usmjeriti ovogodišnju sezonu - u četvrtfinalu kupa na Poljud dolazi Rijeka. </p>','https://hajduk.hr/vijest/u-prodaji-ulaznice-za-utakmice-s-osijekom-i-rijekom/17360');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Brutalist','2025-2-26','18:30h','Kinoteka Zlatna vrata','3,50 €','Film','<p><span style="font-size: 14px;float: none;">Govori o Lászlóu Tóthu (Adrien Brody) mađarskom arhitektu koji, preživjevši Holokaust,  dolazi u SAD i pod okriljem industrijalca Harrisona Leea Van Burena Sr počinje projektirati. Prati njegov život i rad sa detaljnošću koja bi, u rukama manje vještog redatelja, bila naporna i teška. No na koncu, film opravdava kako svoju neobičnu minutažu, tako i sve ostale neobične izbore koje je autor napravio da bi ostvario svoje, možda, i remek-djelo. Rano je to još reći, tek mu je 36 godina. </span><br/></p>','https://zlatnavrata.hr/dogadaj/brutalist/2453');
