INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('KUD Filip Dević','2025-5-4','12:00h','Pjaca','0,00 €','Mjuzza','<p>U sklopu proslave Sudamje, nastupit će i Kulturno-umjetničko društvo Filip Dević, ponosni čuvari splitske tradicije i baštine. Svojim plesom, pjesmom i narodnim nošnjama unijet će duh dalmatinskog folklora u središte blagdanskog slavlja, povezujući prošlost i sadašnjost u čast sv. Dujma i Dana grada Splita.<br/></p>','');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Limena glazba Žrnovnica','2025-5-4','19:00h','Pjaca','0,00 €','Mjuzza','<p>Povodom proslave blagdana sv. Dujma, Limena glazba Žrnovnica &#34;Stjepan Radić&#34; oduševit će publiku izvedbama najpoznatijih skladbi domaćih majstora poput Ive Tijardovića, Jakova Gotovca, Zdenka Runjića i Olivera Dragojevića.<br/></p>','');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Splitski solisti','2025-5-4','20:00h','Pjaca','0,00 €','Mjuzza','<p>Povodom proslave blagdana sv. Dujma, Splitski solisti predvođeni izvanrednim glasovima Majde Goluže i Jurice Karuzze priredit će večer glazbenih užitaka koja će obuhvatiti širok repertoar klasične glazbe, ali i dalmatinskih evergreena. <br/></p>','');
