INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Gjorgji Despodov: Honey, I''m home','2025-8-7','','Dom mladih, Kocka','0,00 €','Izložba','<p>U Galeriji Kluba Kocka u Domu mladih, otvara se izložba &#34;Honey, I’m Home&#34;! Autor izložbe, Gjorgji Despodov, uz pomoć 3D modeliranja, animacije i ispisa istražuje teme pripadnosti, potrage za domom, online zajednica i sigurnih prostora, teme tipične queer zajednici. </p><p><span style="font-size: inherit;background-color: initial;">Izložba se može razgledati radnim danom zaključno s 8. kolovoza u terminima od 18:00 do 21:00.</span></p>','');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Vatroslav Kuliš: Ovo i druga mora','2025-8-7','','Stara gradska vijećnica','0,00 €','Izložba','<p>Ljeto 2025. godine u Staroj gradskoj vijećnici rezervirano je za izložbe s tematikom mora. U Staroj gradskoj vijećnici održat će se izložba jednog od najistaknutijih predstavnika kolorističkog slikarstva u novijoj hrvatskoj povijesti, Vatroslava Kuliša. I<span style="font-size: inherit;background-color: initial;">zložba </span><em style="font-size: inherit;background-color: initial;">&#34;Ovo i druga mora&#34;</em><span style="font-size: inherit;background-color: initial;"> naziv je dobila po knjizi, točnije zbirci pripovjedaka Antuna Šoljana. Tema mora i valova za autora nije novost – vratio joj se nakon 35 godina na novi način i vjeruje da se i tako može ostvariti autorski napredak.</span></p><p><span style="font-size: inherit;background-color: initial;">Ljetno radno vrijeme Stare gradske vijećnice je od utorka do nedjelje u vremenu od od 9:00 do 20:00. Izložba će biti otvorena sve do 14. rujna, stoga ne propustite doživjeti egzotiku koju autor donosi kroz </span><em style="font-size: inherit;background-color: initial;">Ovo i druga mora</em><span style="font-size: inherit;background-color: initial;">.</span></p>','https://www.mgs.hr/');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Vedran Klemens: Ilustracija kao istraživanje, igra, ironija i mašta','2025-8-7','','Galerija umjetnina','0,00 €','Izložba','<p style="font-size: 14.4px;">Izložba ilustracija &#34;Ilustracija kao istraživanje, igra, ironija i mašta&#34; Vedrana Klemensa donosi pregled dinamičnog umjetničkog stvaralaštva koje osvaja neposrednošću, humorom i vizualnom domišljatošću, a pritom s lakoćom komunicira sa širokom publikom.<br/>Najizraženije je obilježje autorova opusa iznimna raznolikost ilustratorskog rukopisa uz suvereno vladanje stilskim jezicima te smiono iskazivanje inventivne osobne imaginacije. Motivski se kreće od duhovito interpretiranih svakodnevnih prizora, glazbe, biomehaničkih bića i satiričnih portreta, s naznakama društvene ironije, stapajući primjerice nostalgiju hrvatskih turističkih plakata šezdesetih s digitalnim jezikom suvremene popularne kulture. Izložba ostaje otvorena do 31. kolovoza.<br/><span style="font-size: inherit;background-color: initial;"><br/></span></p><p style="font-size: 14.4px;"><span style="font-size: inherit;background-color: initial;">Radno vrijeme galerije: </span></p><p style="font-size: 14.4px;">Utorak - Petak: 10:00 - 18:00 h</p><p style="font-size: 14.4px;">Subota - Nedjelja: 10:00 - 14:00 h</p><p style="font-size: 14.4px;">Ponedjeljkom i praznikom zatvoreno. </p>','https://galum.hr/izlozbe/detalj/vedran-klemens-ilustracija-kao-istrazivanje-igra-ironija-i-masta');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Plakat je pozornica','2025-8-7','','kraj HNK','0,00 €','Izložba','<p>Izložbom &#34;Plakat je pozornica&#34; splitskoj se publici predstavlja izbor od nekoliko najupečatljivijih plakata Borisa Bućana, Alema Ćurina i Gorkog Žuvele iz arhiva HNK Split i zbirke Galerije umjetnina, u kustoskom odabiru Marka Goluba.<br/>Svaki od trojice autora pristupa plakatu na jedinstven način: Bućan kroz monumentalnu metaforu, Žuvela kroz dokumentaristički komentar stvarnosti, a Ćurin kroz vizualnu igru i slobodnu asocijaciju. Njihovi plakati su produžetak kazališne scene, prostor u kojem se susreću umjetnost, svakodnevica i mašta.<br/></p><p>Izložba se može razgledati do 14. kolovoza.<br/></p>','https://galum.hr/izlozbe/detalj/plakat-je-pozornica');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Disku(r)sija: Nataša Sardžoska i Nada Topić','2025-8-8','20:00h','Knjižnica Dalmatina','0,00 €','Ostalo','<p><!--StartFragment --><span class="cf0">Go</span><span class="cf0">šće će publici predstaviti svoj spisateljski rad i razgovarati o pisanju, čitanju, prevođenju, promoviranju književnosti, festivalima i rezidencijama, putovanjima, ostancima, motivaciji i nadahnuću, važnosti međunarodnog kulturnog povezivanja i srodnim temama.</span><!--EndFragment --><br/></p>','https://gkmm.hr/dogadaji/diskursija-natasa-sardzoska-i-nada-topic/');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Poplava','2025-8-8','20:30h','Ljetno kino Bačvice','5,00 €','Film','<!--StartFragment --><p class="pf0"><span class="cf0">Očaravajuća priča koja će osvojiti srca svih “velikih i malih” gledatelja jedan je od najhvaljenijih i najnagrađivanijih animiranih filmova prošle godine.</span></p><p class="pf0"><span class="cf0">Nakon što mu je dom uništen velikom poplavom, mačak pronalazi utočište na brodu na kojem se već nalazi nekoliko životinja. Unatoč različitostima, životinje se moraju udružiti kako bi preživjele. Dok usamljeni brodić plovi kroz mistične preplavljene krajolike, njegovi putnici moraju </span><span class="cf0">navigirati</span><span class="cf0"> kroz različite izazove i opasnosti, pritom se pokušavajući adaptirati na jedan sasvim novi svijet.</span></p><p class="pf0"></p><p>Film je sinkroniziran.</p><p></p><!--EndFragment -->','https://kinomediteran.hr/hr/film/54593');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Julije Cezar','2025-8-8','21:00h','Srebrena vrata','od 15,00 €','Predstava','<p><span class="cf0" style="font-size: 17.28px;">Na scenu 71. Splitskog ljeta sti</span><span class="cf1" style="font-size: 17.28px;">že Julije Cezar, kultna politička drama Williama Shakespearea u režiji Q</span><span class="cf2" style="font-size: 17.28px;">ëndrima Rijanija. Ova napeta i bezvremenska pri</span><span class="cf3" style="font-size: 17.28px;">ča o moći, izdaji i borbi za ideale vodi nas u srce antičkog Rima. </span><span class="cf1" style="font-size: 17.28px;">K</span><span class="cf4" style="font-size: 17.28px;">roz splet intriga, unutarnjih sukoba i sudbinskih odluka koje odre</span><span class="cf3" style="font-size: 17.28px;">đuju tijek povijesti. U središtu drame nalaze se Brut i Kasije, dvojica senatora koji, vjerujući da djeluju za dobrobit Rima, pokreću događaje koji će zauvijek promijeniti lice Republike.</span><br/></p>','https://www.splitsko-ljeto.hr/71-splitsko-ljeto/predstave/detalj/71slj-julije-cezar');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Doris Dragović','2025-8-8','21:00h','Solin, Gradina','25,00 €','Mjuzza','<!--StartFragment --><p class="pf0"><span class="cf0">U sklopu 30. Solinskog kulturnog ljeta u Gradinu sti</span><span class="cf0">že Doris Dragović! </span></p><p class="pf0"><span class="cf1">U svojoj vi</span><span class="cf2">še od četiri desetljeća dugoj karijeri, jedna od najvećih hrvatskih glazbenih zvijezda, svojoj je publici podarila brojne prekrasne pjesme koje se rado pamte i pjevaju i nakon toliko godina. </span></p><p class="pf0"><span class="cf1">“</span><span class="cf2">Željo moja” , “Marija Magdalena , “Krivi ljudi” , “Jedini” , “Malo mi za sriću triba” , “Jubavi rič” samo su neke od predivnih pjesama koje ćete zajedno s Doris imati priliku zapjevati u solinskoj Gradini. </span></p><p class="pf0"><span class="cf1">Ne propustite biti dio ove ve</span><span class="cf2">čeri uz dobro vam znane melodije koje bude ljubav!</span></p><!--EndFragment -->','https://shop.adriaticket.com/event-details/4037/doris-dragovic');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Južina','2025-8-8','22:30h','Ljetno kino Bačvice','5,00 €','Film','<!--StartFragment --><p class="pf0"><span class="cf0">Dok južina trese Splitom, životi stanara oronule četverokatnice počinju se ispreplitati. Suspendirani profesor, paranoična starica, manipulativna majka-vjernica, neuspješni reper i nasilni bivši dečko sudaraju se u mračnoj komediji nesporazuma, sitnih spletki i rastućeg kaosa gdje su čak i </span><span class="cf1">„heroji“ zlikovci u o</span><span class="cf0">čima svojih susjeda.</span></p><p class="pf0"><span class="cf0">Živopisna crna komedija redatelja i scenarista Ante Marina premijerno je prikazana na Pulskom filmskom festivalu.</span></p><!--EndFragment -->','https://kinomediteran.hr/hr/film/58791');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Vojin Hraste: Igračka Vjetrova','2025-8-8','','Dom mladih, Galerija MKC','0,00 €','Izložba','<p>Najnoviji umjetnički projekt Vojina Hraste polazi od ideje izrade plovila kojeg pokreće vjetrenjača čija se energija prenosi na brodski propeler omogućujući mu plovidbu suprotno smjeru vjetra, u samo srce oluje.</p><p>Izložba traje od 25.07 do 13.08. Radno vrijeme galerije 9:00- 2:00 i 18:00-21:00, svakog dana osim nedjelje i blagdana.</p><p></p>','https://www.facebook.com/MKCSplit');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Alison Braun: IN THE PIT','2025-8-8','','Fotoklub Split','0,00 €','Izložba','<p>Izložba fotografija IN THE PIT donosi nam presjek jedne od najznačajnijih razdoblja u povijesti glazbe, eksplozije američke underground punk rock heavy metal i grunge scene kasnih osamdesetih i s početka devedesetih. Izložba u Galeriji fotografije Fotokluba Split donosi izbor njenih omiljenih fotografija. Na izložbi se tako mogu vidjeti kultne fotografije bendova poput Dead Kennedysa, Black Flaga, GBH-a, Dischargea, Bad Brainsa, Ramonesa i brojnih drugi. Alison Braun pionirka je koncertne i glazbene fotografije. Više od četiri desetljeća provela je snimajući nastupe uživo i bilježeći kulturni značaj američke punk rock, hardcore, heavy metal i grunge glazbene scene osamdesetih i devedesetih godina prošloga stoljeća.</p><p>Izložba u Fotoklubu Split ostaje otvorena do 25. kolovoza i može se pogledati u radno vrijeme Galerije fotografije, svakim radnim danom od 8 do 21 sat.</p>','https://fotoklubsplit.hr/');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Virgilije Nevjestić: Umjetnost grafike - Metamorfoza jednog sna','2025-8-8','','Sveučilišna galerija','0,00 €','Izložba','<p>Izložba obuhvaća sedamdesetak vrhunskih grafičkih ostvarenja Virgilija Nevjestića, umjetnika iznimne imaginacije i vrhunskog vizualnog izraza, nastalih između 1963. i kraja 1980-ih godina, pružajući uvid u Nevjestićev stilistički i formalni razvoj - od crno-bijelih radova inspiriranih rodnim krajem, preko nadrealističkih figurativno-asocijativnih scena, pa sve do iznimno rafiniranih grafičkih ciklusa koji prate umjetnikov prepoznatljiv izraz.</p><p>Događanje organiziraju Sveučilište u Splitu i Zaklada Virgilije Nevjestić u okviru obilježavanja 90. obljetnice rođenja Virgilija Nevjestića.<br/></p><p>Sveučilišna galerija je otvorena radnim danima od 9:00 do 12:00 i od 17:00 do 20:00 sati.</p>','nist.hr/u-prepunoj-sveucilisnoj-galeriji-svecano-otvorena-izlozba-virgilija-nevjestica-jednog-od-najvaznijih-graficara-20-stoljeca/20880');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Ivona Velkovska: Osvjetljenje, otkrivanje, oslobađanje','2025-8-8','','Studio-21-','0,00 €','Izložba','<p>U galeriji Studio-21- otvara se samostalna izložba makedonske umjetnice Ivone Velkovske pod nazivom „Osvjetljavanje, otkrivanje, oslobađanje“.<br/>Izložba donosi recentni ciklus radova koji kroz apstraktni likovni jezik, bogatu simboliku i slojevitu kompoziciju istražuje prostore unutarnjeg svijeta, duhovne percepcije i osobne transformacije.<br/><br/>Galerija je preko ljetne sezone otvorena radnim danima od 18:30 do 21:30. Subotom, nedjeljom i praznicima je zatvoreno.</p>','https://www.facebook.com/people/Studio-21-/100064899662332/#');
INSERT INTO "EVENT" (NAME, DATE, TIME, LOCATION, PRICE, CATEGORY, DESCRIPTION, LINK) VALUES ('Gjorgji Despodov: Honey, I''m home','2025-8-8','','Dom mladih, Kocka','0,00 €','Izložba','<p>U Galeriji Kluba Kocka u Domu mladih, otvara se izložba &#34;Honey, I’m Home&#34;! Autor izložbe, Gjorgji Despodov, uz pomoć 3D modeliranja, animacije i ispisa istražuje teme pripadnosti, potrage za domom, online zajednica i sigurnih prostora, teme tipične queer zajednici. </p><p><span style="font-size: inherit;background-color: initial;">Izložba se može razgledati radnim danom zaključno s 8. kolovoza u terminima od 18:00 do 21:00.</span></p>','');
